import { MapContainer, TileLayer, Circle, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import React from 'react';

// Icono custom para los cargadores (mejor visibilidad)
const chargerIcon = new L.Icon({
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
  shadowSize: [41, 41]
});

// Utilidad para calcular color según volumen
function getColor(vol, max) {
  if (max === 0) return '#ccc';
  const ratio = vol / max;
  if (ratio > 0.66) return '#e53e3e'; // rojo
  if (ratio > 0.33) return '#f6ad55'; // naranja
  return '#38a169'; // verde
}

export function MapaZonasInfluencia({ cargadores }) {
  // Centrar el mapa en el centroide de los cargadores
  const center = cargadores.length
    ? [
        cargadores.reduce((a, c) => a + c.lat, 0) / cargadores.length,
        cargadores.reduce((a, c) => a + c.lng, 0) / cargadores.length
      ]
    : [-34.6, -58.4]; // fallback: Buenos Aires

  return (
    <MapContainer center={center} zoom={7} style={{ height: 320, width: '100%' }} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; OpenStreetMap contributors'
        url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
      />
      {cargadores.map((c, i) => (
        <Circle
          key={c.id || i}
          center={[c.lat, c.lng]}
          radius={100000} // 100km
          pathOptions={{ color: '#f6ad55', fillColor: '#f6ad55', fillOpacity: 0.18, weight: 2 }}
        />
      ))}
      {cargadores.map((c, i) => (
        <Marker key={'m'+(c.id||i)} position={[c.lat, c.lng]} icon={chargerIcon}>
          <Popup>
            <strong>{c.name}</strong>
            <br />Lat: {c.lat.toFixed(4)}<br />Lng: {c.lng.toFixed(4)}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export function MapaVolumenUso({ cargadores, volumenes }) {
  // volumenes: { [charger_id]: minutos }
  const maxVol = Math.max(...Object.values(volumenes), 1);
  const center = cargadores.length
    ? [
        cargadores.reduce((a, c) => a + c.lat, 0) / cargadores.length,
        cargadores.reduce((a, c) => a + c.lng, 0) / cargadores.length
      ]
    : [-34.6, -58.4];
  return (
    <MapContainer center={center} zoom={7} style={{ height: 320, width: '100%' }} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; OpenStreetMap contributors'
        url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
      />
      {cargadores.map((c, i) => (
        <Circle
          key={c.id || i}
          center={[c.lat, c.lng]}
          radius={12000}
          pathOptions={{ color: getColor(volumenes[c.id]||0, maxVol), fillColor: getColor(volumenes[c.id]||0, maxVol), fillOpacity: 0.7, weight: 2 }}
        >
          <Popup>
            <strong>{c.name}</strong><br/>
            Minutos: {volumenes[c.id]||0}
          </Popup>
        </Circle>
      ))}
    </MapContainer>
  );
}
