import React from 'react';
import ConnectorItem from './ConnectorItem';

function ChargersList({ chargers }) {
  // Ordenar cargadores por nombre
  const sortedChargers = [...chargers].sort((a, b) => a.name.localeCompare(b.name));

  return (
    <div className="space-y-6">
      {sortedChargers.map((charger) => (
        <div key={charger.name} className="bg-white rounded shadow p-4">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-2">
            <div>
              <h2 className="text-lg font-bold text-gray-800">{charger.name}</h2>
              <div className="text-xs text-gray-500">Lat: {charger.latitude} | Lon: {charger.longitude}</div>
            </div>
            <div className="mt-2 md:mt-0">
              <span className={`inline-block px-3 py-1 rounded-full text-xs font-semibold ${
                charger.status === 'Available' ? 'bg-green-100 text-green-800' :
                charger.status === 'Charging' ? 'bg-orange-100 text-orange-800' :
                'bg-gray-200 text-gray-600'
              }`}>
                {charger.status}
              </span>
            </div>
          </div>
          <div>
            {/* Ordenar conectores: Charging primero, luego Available, luego Unavailable, luego por tipo */}
            {charger.connectors && [...charger.connectors]
              .sort((a, b) => {
                const stateOrder = {
                  Charging: 0,
                  Available: 1,
                  Unavailable: 2,
                };
                if (a.state !== b.state) {
                  return (stateOrder[a.state] || 3) - (stateOrder[b.state] || 3);
                }
                return a.type.localeCompare(b.type);
              })
              .map((connector, idx) => (
                <ConnectorItem key={connector.type + '-' + idx} connector={connector} />
              ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default ChargersList;
