import React, { useEffect, useState } from 'react';
import ChargersList from './ChargersList';
import SessionsList from './SessionsList';
import './index.css';

function App() {
  const [chargers, setChargers] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState('chargers'); // 'chargers' | 'sessions'

  // Fetch chargers from backend every 30 seconds
  useEffect(() => {
    let interval;
    const fetchChargers = async () => {
      setLoading(true);
      try {
        const res = await fetch('/api/chargers');
        const data = await res.json();
        // Filtrar cargadores con al menos un conector de 60kW o más
        const filteredChargers = (data.chargers || []).filter(
          charger => charger.cnns && charger.cnns.some(conn => conn.power >= 60)
        );
        setChargers(filteredChargers);
        setError(null);
      } catch (err) {
        setError('Error al obtener los cargadores');
      } finally {
        setLoading(false);
      }
    };
    fetchChargers();
    interval = setInterval(fetchChargers, 30000);
    return () => clearInterval(interval);
  }, []);

  // Fetch sessions when switching to sessions view
  useEffect(() => {
    if (view === 'sessions') {
      setLoading(true);
      fetch('/api/sessions')
        .then(res => res.json())
        .then(data => {
          setSessions(data.sessions || []);
          setError(null);
        })
        .catch(() => setError('Error al obtener las sesiones'))
        .finally(() => setLoading(false));
    }
  }, [view]);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-2xl font-bold mb-6 text-center text-gray-900">EV Chargers Monitor</h1>
      <div className="flex justify-center gap-4 mb-6">
        <button
          className={`px-4 py-2 rounded font-semibold ${view === 'chargers' ? 'bg-blue-600 text-white' : 'bg-white border text-blue-600'}`}
          onClick={() => setView('chargers')}
        >
          Cargadores
        </button>
        <button
          className={`px-4 py-2 rounded font-semibold ${view === 'sessions' ? 'bg-blue-600 text-white' : 'bg-white border text-blue-600'}`}
          onClick={() => setView('sessions')}
        >
          Sesiones
        </button>
      </div>
      {loading && <div className="text-center text-gray-500">Cargando...</div>}
      {error && <div className="text-center text-red-500">{error}</div>}
      {!loading && !error && view === 'chargers' && <ChargersList chargers={chargers} />}
      {!loading && !error && view === 'sessions' && <SessionsList sessions={sessions} onBack={() => setView('chargers')} />}
    </div>
  );
}

export default App;
